  --spacing-xs: 0.25rem;
  --spacing-sm: 0.5rem;
  --spacing-md: 1rem;
  --spacing-lg: 1.5rem;
  --spacing-xl: 2rem;
}

/* Import notification styles */
@import url('notifications.css');

/* Base styling */
body {
    font-family: 'Arial', sans-serif;
    margin: 0;
    padding: 0;
    background-color: #f5f5f5;
}

* {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

body {
  font-family: 'Poppins', sans-serif;
  color: var(--text);
  background: linear-gradient(135deg, var(--bg-start), var(--bg-end));
  min-height: 100vh;
  line-height: 1.5;
}

/* ‚Äî LOGO HEADER ‚Äî */
.logo-header {
  text-align: left;
  padding: 2rem 0 1rem;
}
.logo-header img {
  height: 64px;
  filter: drop-shadow(0 4px 6px rgba(0,0,0,0.1));
}

/* Modern Navigation Bar - Space Efficient */
    .modern-nav {
      display: flex;
      justify-content: space-between;
      align-items: center;
      background: linear-gradient(135deg, white 0%, rgba(77, 208, 225, 0.05) 100%);
      padding: 8px 16px; /* Reduced from 16px 32px */
      box-shadow: 0 2px 8px rgba(77, 208, 225, 0.1); /* Reduced shadow */
      border-bottom: 1px solid var(--cully-primary); /* Reduced from 2px */
      margin-bottom: 0;
      width: 100%;
      box-sizing: border-box;
      position: sticky;
      top: 0;
      z-index: 1000;
      overflow: hidden;
      transition: all 0.3s ease;
    }

    /* Dynamic navigation sizing */
    @media (max-width: 1200px) {
      .modern-nav {
        padding: 6px 12px;
      }

      .brand-text {
        font-size: 16px;
      }

      .nav-actions {
        gap: 8px;
      }
    }

    @media (max-width: 768px) {
      .modern-nav {
        padding: 4px 8px;
        flex-wrap: wrap;
        gap: 4px;
      }

      .brand-text {
        display: none; /* Hide brand text on small screens */
      }

      .brand-logo {
        height: 28px; /* Smaller logo */
      }
    }

    @media (max-width: 480px) {
      .modern-nav {
        padding: 2px 4px;
        min-height: 40px;
      }
    }

.nav-brand {
  display: flex;
  align-items: center;
  gap: 12px;
}

.brand-logo {
  height: 40px;
  width: auto;
}

.brand-text {
  font-size: 18px;
  font-weight: 600;
  color: var(--text-primary);
}

.nav-actions {
  display: flex;
  align-items: center;
  gap: 8px;
  flex-wrap: nowrap;
}

/* Quick Actions */
.quick-table-access {
  position: relative;
  margin-right: 16px;
}

.quick-access-btn {
  display: inline-flex;
  align-items: center;
  gap: 8px;
  padding: 8px 16px;
  background: linear-gradient(135deg, var(--cully-accent) 0%, var(--cully-primary) 100%);
  color: white;
  border: none;
  border-radius: var(--border-radius-sm);
  font-weight: 600;
  font-size: 14px;
  cursor: pointer;
  transition: var(--transition);
  box-shadow: 0 2px 8px rgba(77, 208, 225, 0.25);
}

.quick-access-btn:hover {
  background: linear-gradient(135deg, var(--cully-accent-light) 0%, var(--cully-primary-light) 100%);
  transform: translateY(-2px);
  box-shadow: 0 4px 15px rgba(77, 208, 225, 0.4);
}

.quick-access-dropdown {
  display: none;
  position: absolute;
  top: 100%;
  left: 0;
  background: white;
  border: 1px solid var(--border-color);
  border-radius: var(--border-radius);
  box-shadow: 0 8px 25px rgba(0,0,0,0.15);
  min-width: 200px;
  z-index: 1000;
  opacity: 0;
  visibility: hidden;
  transform: translateY(-10px);
  transition: all 0.3s ease;
  margin-top: 8px;
}

.quick-access-dropdown.show {
  display: block;
  opacity: 1;
  visibility: visible;
  transform: translateY(0);
}

.quick-table-access.open .quick-access-dropdown {
  display: block;
  opacity: 1;
  visibility: visible;
  transform: translateY(0);
}

.quick-access-dropdown a {
  display: flex;
  align-items: center;
  gap: 12px;
  padding: 12px 16px;
  color: var(--text-primary);
  text-decoration: none;
  transition: var(--transition);
  border-bottom: 1px solid var(--border-color);
}

.quick-access-dropdown a:last-child {
  border-bottom: none;
}

.quick-access-dropdown a:hover {
  background: var(--bg-secondary);
  color: var(--primary);
}

.quick-access-dropdown i {
  width: 16px;
  text-align: center;
}

/* Special styling for logout option */
.quick-access-dropdown a[href*="logout"] {
  color: #ef4444;
}

.quick-access-dropdown a[href*="logout"]:hover {
  background: #fee2e2;
  color: #dc2626;
}

.quick-access-dropdown a[href*="logout"] i {
  color: #ef4444;
}

/* User Profile Section */
.user-profile {
  display: flex;
  align-items: center;
  gap: 12px;
  position: relative;
}

.user-avatar {
  width: 40px;
  height: 40px;
  background: linear-gradient(135deg, var(--cully-primary), var(--cully-primary-dark));
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-size: 18px;
  box-shadow: 0 2px 10px rgba(77, 208, 225, 0.3);
  border: 2px solid white;
}

.user-details {
  cursor: pointer;
  padding: 8px 12px;
  border-radius: var(--border-radius-sm);
  transition: var(--transition);
  min-width: 120px;
}

.user-details:hover {
  background: var(--bg-secondary);
}

.user-name {
  font-weight: 600;
  font-size: 14px;
  color: var(--text-primary);
  margin-bottom: 2px;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  max-width: 150px;
}

.user-role {
  font-size: 12px;
  color: var(--text-secondary);
}

/* User Dropdown Menu */
.user-dropdown-menu {
  position: absolute;
  top: 100%;
  right: 0;
  background: white;
  border: 1px solid var(--border-color);
  border-radius: var(--border-radius);
  box-shadow: 0 8px 25px rgba(0,0,0,0.15);
  min-width: 200px;
  z-index: 1000;
  opacity: 0;
  visibility: hidden;
  transform: translateY(-10px);
  transition: all 0.3s ease;
  margin-top: 8px;
}

.user-dropdown-menu.show {
  opacity: 1;
  visibility: visible;
  transform: translateY(0);
}

.user-dropdown-menu a {
  display: flex;
  align-items: center;
  gap: 12px;
  padding: 12px 16px;
  color: var(--text-primary);
  text-decoration: none;
  transition: var(--transition);
  border-bottom: 1px solid var(--border-color);
}

.user-dropdown-menu a:last-child {
  border-bottom: none;
}

.user-dropdown-menu a:hover {
  background: var(--bg-secondary);
  color: var(--primary);
}

.user-dropdown-menu i {
  width: 16px;
  text-align: center;
}

.dropdown-divider {
  height: 1px;
  background: var(--border-color);
  margin: 4px 0;
}

/* ‚Äî WELCOME SCREEN ‚Äî */
.welcome-page {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 100vh;
  text-align: center;
  background: linear-gradient(135deg, var(--primary) 0%, #2c5aa0 100%);
  color: white;
  padding: 2rem;
}
.welcome-page h2 {
  font-size: 2.5rem;
  color: var(--primary-light);
  margin: 1rem 0;
}
.welcome-page p {
  margin-bottom: 2rem;
  color: #555;
}

/* ‚Äî BUTTONS ‚Äî */
.btn-primary,
.btn-secondary {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  font-weight: 600;
  border: none;
  padding: 0.75rem 1.5rem;
  border-radius: var(--border-radius);
  cursor: pointer;
  transition: var(--transition);
  text-decoration: none;
}
.btn-primary {
  background: linear-gradient(135deg, var(--cully-primary) 0%, var(--cully-secondary) 100%);
  color: white;
  box-shadow: 0 6px 20px rgba(77, 208, 225, 0.3);
  border: none;
}
.btn-primary:hover {
  background: linear-gradient(135deg, var(--cully-primary-light) 0%, var(--cully-secondary-light) 100%);
  transform: translateY(-2px);
  box-shadow: 0 8px 25px rgba(77, 208, 225, 0.4);
}
.btn-secondary {
  background: transparent;
  color: var(--primary-light);
  border: 2px solid var(--primary-light);
  box-shadow: 0 4px 12px var(--shadow-color);
}
.btn-secondary:hover {
  background: rgba(47,132,189,0.1);
  transform: translateY(-1px);
}

/* Save Progress Button */
.save-progress-btn {
  display: inline-flex;
  align-items: center;
  gap: 8px;
  padding: 8px 16px;
  background: linear-gradient(135deg, var(--cully-secondary) 0%, var(--cully-accent) 100%);
  color: white;
  border: none;
  border-radius: var(--border-radius-sm);
  font-weight: 600;
  font-size: 14px;
  cursor: pointer;
  transition: var(--transition);
  margin-right: 12px;
  white-space: nowrap;
  box-shadow: 0 2px 8px rgba(0, 188, 212, 0.25);
}

.save-progress-btn:hover {
  background: linear-gradient(135deg, var(--cully-secondary-light) 0%, var(--cully-accent-light) 100%);
  transform: translateY(-2px);
  box-shadow: 0 4px 15px rgba(0, 188, 212, 0.4);
}

.save-progress-btn i {
  font-size: 14px;
}

/* ‚Äî FULL-WIDTH CONTAINER SYSTEM ‚Äî */
.container {
  max-width: none;
  margin: 0;
  padding: 0;
  background: transparent;
  border-radius: 0;
  box-shadow: none;
  backdrop-filter: none;
  overflow-x: hidden;
  width: 100vw;
  box-sizing: border-box;
}

/* Override any existing container restrictions */
body .container {
  width: 100vw !important;
  max-width: none !important;
  margin: 0 !important;
  padding: 0 !important;
}

/* Body adjustments for full-width design */
body.dashboard-layout {
  margin: 0;
  padding: 0;
  width: 100vw;
  overflow-x: hidden;
  box-sizing: border-box;
  background: linear-gradient(135deg, var(--cully-primary-light) 0%, var(--cully-secondary-light) 50%, var(--cully-primary-light) 100%);
  min-height: 100vh;
}

/* Admin container should use full viewport */
.admin-container {
      max-width: none;
      margin: 0;
      padding: 0;
      width: 100vw;
      box-sizing: border-box;
    }

    /* Full Width Layout Overrides */
    .horizontal-progress-bar {
      width: 100%;
      margin: 16px 0;
      padding: 20px;
      box-sizing: border-box;
    }

    .full-width-content {
      width: 100%;
      margin: 0;
      padding: 0;
      box-sizing: border-box;
    }

    .form-step {
      padding: 24px;
      margin: 0;
      width: 100%;
      box-sizing: border-box;
    }
form fieldset {
  border: none;
  margin-bottom: 2rem;
  display: none;
  padding: 1rem 0;
}
form fieldset.active {
  display: block;
  animation: fadeIn 0.5s ease-out;
}
.step-legend {
      font-size: 1.3rem; /* Reduced from 1.75rem */
      font-weight: 700;
      color: var(--cully-primary);
      margin-bottom: 12px; /* Reduced from 24px */
      display: flex;
      align-items: center;
      gap: 8px; /* Reduced from 12px */
    }

    .step-legend i {
      color: var(--cully-secondary);
      font-size: 1.1rem; /* Smaller icon */
    }

    /* Enhanced Form Sections */
    .form-section {
      background: linear-gradient(135deg, rgba(255, 255, 255, 0.95) 0%, rgba(248, 250, 252, 0.9) 100%);
      border: 2px solid rgba(77, 208, 225, 0.15);
      border-radius: var(--border-radius-lg);
      padding: 32px;
      margin: 24px 0;
      box-shadow: 0 8px 32px rgba(77, 208, 225, 0.1);
      backdrop-filter: blur(10px);
      transition: all 0.3s ease;
    }

    .form-section:hover {
      border-color: rgba(77, 208, 225, 0.3);
      box-shadow: 0 12px 40px rgba(77, 208, 225, 0.15);
      transform: translateY(-2px);
    }

    .form-section h4 {
      color: var(--cully-primary);
      font-size: 1.3rem;
      font-weight: 700;
      margin-bottom: 20px;
      display: flex;
      align-items: center;
      gap: 12px;
      padding-bottom: 12px;
      border-bottom: 2px solid var(--cully-primary-light);
    }

    .form-section h4 i {
      font-size: 1.4rem;
      color: var(--cully-secondary);
    }

/* ‚Äî PROGRESS BAR ‚Äî */
nav.progress {
  display: flex;
  justify-content: center;
  gap: 1rem;
  margin: 1.5rem 0;
}
.progress-step {
  text-align: center;
  cursor: pointer;
  transition: var(--transition);
}
.progress-step.disabled {
  opacity: 0.3;
}
.progress-step .circle {
  width: 42px;
  height: 42px;
  margin: 0 auto 0.3rem;
  border-radius: 50%;
  background: #e0e0e0;
  color: #555;
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: 600;
}
.progress-step.active .circle {
  background: linear-gradient(135deg, var(--cully-primary) 0%, var(--cully-secondary) 100%);
  color: #fff;
  box-shadow: 0 0 20px rgba(77, 208, 225, 0.5);
  border: 2px solid var(--cully-primary-light);
}
.progress-step span {
  display: block;
  font-size: 0.85rem;
  color: var(--text);
}

/* ‚Äî LABELS & INPUTS ‚Äî */
label {
  display: block;
  margin-top: 1.2rem;
  font-weight: 500;
}
.required {
  color: var(--accent);
}
input, textarea, select {
  width: 100%;
  margin-top: 0.4rem;
  padding: 0.75rem 1rem;
  font-size: 1rem;
  border: none;
  border-radius: var(--border-radius);
  background: #f5f5f5;
  box-shadow: inset 0 2px 5px var(--shadow-color);
  transition: var(--transition);
}
input:focus, textarea:focus, select:focus {
  outline: none;
  box-shadow: 0 0 0 3px rgba(47,132,189,0.2);
}
select {
  appearance: none;
  background-image: url("data:image/svg+xml;charset=US-ASCII,%3Csvg width='10' height='5' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='M0 0l5 5 5-5z' fill='%23666'/%3E%3C/svg%3E");
  background-repeat: no-repeat;
  background-position: right 1rem center;
  background-size: 10px 5px;
}
textarea { min-height: 100px; resize: vertical; }

/* ‚Äî ERROR TEXT ‚Äî */
.error {
  display: none;
  color: #e53935;
  font-size: 0.85rem;
  margin-top: 0.3rem;
}
input:invalid + .error,
textarea:invalid + .error {
  display: block;
}

/* ===== COMPLETE RESPONSIVE DESIGN SYSTEM ===== */

/* Main Layout - Vertical Sidebar with Full Width Content */
    .main-layout {
      display: flex;
      flex-direction: row;
      min-height: calc(100vh - 80px);
      width: 100vw;
      max-width: 100vw;
      margin: 0;
      gap: 0;
      padding: 0;
      overflow-x: hidden;
    }

    /* Vertical Progress Sidebar */
    .app-sidebar {
      width: 320px;
      min-width: 320px;
      flex-shrink: 0;
      background: rgba(255, 255, 255, 0.98);
      backdrop-filter: blur(10px);
      border-radius: 0 16px 16px 0;
      padding: 24px;
      height: calc(100vh - 80px);
      box-shadow: 4px 0 20px rgba(0,0,0,0.08);
      border: 1px solid var(--border-color);
      border-left: none;
      box-sizing: border-box;
      overflow-y: auto;
      position: sticky;
      top: 0;
    }

    /* Main Content Area - Full Width Remaining Space */
    .app-content {
      flex: 1;
      min-width: 0;
      width: calc(100vw - 320px);
      max-width: calc(100vw - 320px);
      background: rgba(255, 255, 255, 0.98);
      backdrop-filter: blur(15px);
      border-radius: 16px 0 0 16px;
      padding: 24px;
      box-shadow: 0 4px 20px rgba(0,0,0,0.08);
      border: 1px solid var(--border-color);
      border-right: none;
      box-sizing: border-box;
      overflow-x: auto;
      overflow-y: auto;
      height: calc(100vh - 80px);
    }

    /* Vertical Progress Steps */
    .progress-nav {
      display: flex;
      flex-direction: column;
      gap: 12px;
      padding: 0;
      width: 100%;
      box-sizing: border-box;
    }

    .progress-step {
      display: flex;
      flex-direction: row;
      align-items: center;
      gap: 16px;
      padding: 16px;
      border-radius: var(--border-radius-sm);
      cursor: pointer;
      transition: var(--transition);
      border: 1px solid transparent;
      text-align: left;
      width: 100%;
      background: rgba(255, 255, 255, 0.9);
      box-shadow: 0 2px 8px rgba(0,0,0,0.05);
      position: relative;
    }

.step-circle {
      width: 36px;
      height: 36px;
      border-radius: 50%;
      background: #e2e8f0;
      color: var(--text-secondary);
      display: flex;
      align-items: center;
      justify-content: center;
      font-weight: 600;
      font-size: 16px;
      transition: var(--transition);
      flex-shrink: 0;
      border: 2px solid #f1f5f9;
    }

    .step-info {
      display: flex;
      flex-direction: column;
      flex: 1;
      min-width: 0;
    }

    .step-title {
      font-weight: 600;
      font-size: 14px;
      margin-bottom: 4px;
      line-height: 1.2;
      color: var(--text-primary);
    }

    .step-description {
      font-size: 12px;
      opacity: 0.8;
      line-height: 1.3;
      color: var(--text-secondary);
    }

    .progress-step:hover {
      background: rgba(77, 208, 225, 0.05);
      border-color: rgba(77, 208, 225, 0.2);
      transform: translateY(-1px);
    }

    .progress-step.active {
      background: rgba(77, 208, 225, 0.1);
      border-color: var(--cully-primary-light);
    }

    .progress-step.active .step-circle {
      background: linear-gradient(135deg, var(--cully-primary), var(--cully-primary-dark));
      color: white;
      box-shadow: 0 4px 15px rgba(77, 208, 225, 0.4);
      border-color: var(--cully-primary);
    }

    .progress-step.active .step-title {
      color: var(--cully-primary);
      font-weight: 700;
    }

    .progress-step.active .step-description {
      color: var(--cully-primary-dark);
      opacity: 0.9;
    }

    .progress-step.completed .step-circle {
      background: var(--cully-primary-light);
      color: white;
      border-color: var(--cully-primary-light);
    }

    .progress-step.completed .step-title {
      color: var(--cully-primary-dark);
    }

/* Space-Efficient Cards */
    .card {
      background: white;
      border-radius: var(--border-radius-sm);
      border: 1px solid var(--border-color);
      margin: 12px 0; /* Reduced from 24px */
      overflow: hidden;
      box-shadow: 0 1px 6px rgba(0, 0, 0, 0.04); /* Reduced shadow */
    }

    .card-header {
      background: linear-gradient(135deg, var(--cully-primary) 0%, var(--cully-primary-light) 100%);
      color: white;
      padding: 10px 16px; /* Reduced from 16px 24px */
      font-weight: 600;
      font-size: 14px; /* Slightly smaller */
      display: flex;
      justify-content: space-between;
      align-items: center;
    }

    .card-body {
      padding: 12px; /* Reduced from 24px */
    }

/* Tables - Maximum Width Utilization */
    .table-responsive, .table-container {
      position: relative;
      margin: 6px 0; /* Reduced margin */
      border-radius: var(--border-radius-sm);
      box-shadow: 0 1px 4px rgba(0,0,0,0.06);
      background: white;
      width: 100%;
      max-width: 100%;
      overflow-x: auto;
      -webkit-overflow-scrolling: touch;
    }

    /* Base Table Styles - Full Width */
    table {
      width: 100%;
      min-width: 100%;
      border-collapse: collapse;
      background: white;
      border-radius: var(--border-radius-sm);
      table-layout: fixed; /* Fixed layout for better control */
      font-size: 12px; /* Optimized font size */
    }

