      font-size: 11px; /* Reduced from 14px */
      border-bottom: 1px solid var(--cully-primary-dark);
      white-space: nowrap;
      position: sticky;
      top: 0;
      z-index: 10;
    }

    /* Compact Table Cells */
    .digital-signals td,
    .digital-outputs td,
    .analogue-inputs td,
    .analogue-outputs td,
    .approvals td,
    .documents td,
    .pretest-table td,
    .key-components td,
    .ip-records td,
    .modbus-digital td,
    .modbus-analogue td,
    .process-tests td,
    .scada-verification td,
    .trends-testing td,
    .alarm-signals td {
      padding: 6px 4px; /* Reduced from 14px 16px */
      border-bottom: 1px solid #f1f5f9;
      color: var(--text-primary);
      font-size: 12px; /* Reduced from 14px */
      vertical-align: top;
    }

    /* Alternating row colors for better readability */
    tbody tr:nth-child(even) {
      background: #f8f9fa;
    }

    tbody tr:hover {
      background: rgba(77, 208, 225, 0.08);
    }

/* Professional Form Controls */
tbody td input,
tbody td textarea,
tbody td select {
  width: 100%;
  min-width: 0;
  padding: 10px 12px;
  border: 1px solid #e2e8f0;
  border-radius: 6px;
  font-size: 13px;
  margin: 0;
