    margin-left: 10px;
}

.toast-close:hover {
    color: #333;
}

/* Responsive Design */
@media (max-width: 768px) {
    .notification-dropdown {
        width: 300px;
        right: -50px;
    }

    .notification-toast {
        right: 10px;
        left: 10px;
        min-width: auto;
    }
}

